"use strict";
const youtube = require('./YouTube.js');
const {getTrackID, getPlaylistID, getAlbumID, getAuthorID} = require('./Spotify/getID.js');
const {getTrack, getPlaylist, getAlbum, isValidTrack} = require('./Spotify/getInfo.js');

const spotify = (url, options = {}) => {
    return new Promise((res) => {
        spotify.getTrack(url).then(song => youtube.searchVideos(song.title).then(Url => res(youtube(Url[0].url, options))));
    });
};

spotify.getTrack = getTrack;
spotify.getPlaylist = getPlaylist;
spotify.getTrackID = getTrackID;
spotify.getPlaylistID = getPlaylistID;
spotify.getAlbumID = getAlbumID;
spotify.getArtistID = getAuthorID;
spotify.getAlbum = getAlbum;
spotify.isValidTrack = isValidTrack;
module.exports = spotify;