"use strict";
let youtubeStr = /^(https?:\/\/)?(www\.)?(m\.)?( )?(youtube\.com|youtu\.?be)\/.+$/gi;

module.exports = {
    getVideoID: (url) => {
        if (!url.match(youtubeStr)) return 'This url is not a youtube url';
        if (typeof url !== 'string') return 'Url is not string';

        if (url.match(/youtube/)) {
            if (url.match(/list/)) return url.split('?v=')[1].split('&list=')[0];
            else if (url.match(/shorts/)) return url.split('shorts/')[1];
            else return url.split('?v=')[1];
        } else if (url.match(/youtu/)) return url.split('youtu')[1].split('/')[1];

        return 'This url is not youtube';
    },
    getPlaylistID: (url) => {
        if (!url.match(youtubeStr)) return 'This url is not a youtube url';
        if (typeof url !== 'string') return 'Url is not string';

        if (url.match(/youtube/)) {
            if (url.match(/playlist/)) return url.split('playlist?list=')[1];
        } else return 'This url is not youtube';
    }
};